var mysql=require('mysql');
 
function connectServer(){
    var client=mysql.createConnection({
        host:'localhost',
        user:'root',
        password:'123456',
        database:'nodejs'
    })

    return client;
}


function  selectFun(client,username,callback){
    //client为一个mysql连接对象
    client.query('select st_password from user where st_username="'+username+'"',function(err,results,fields){
        if(err) throw err;

        callback(results);
    });
}

exports.connect = connectServer;
exports.selectFun  = selectFun;